import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import Header from '../Header/Header.jsx';
import Footer from '../Footer/Footer.jsx';
import './NumbersTrainPage.css';
import { vocabulary } from "../../utils/constants.js";
import arrow from '../../images/arrow2.png';
import { removeCurrentDeck } from '../../services/actions/currentDeck.js';

function NumbersTrainPage() {

  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [words, setWords] = useState([]); /*все числа*/
  const [currentNum, setCurrentNum] = useState(null);
  const [showNum, setShowNum] = useState(false);
  const [isCorrect, setIsCorrect] = useState(false);

  const [repeatingForm, setMeaning] = useState({ word: '' });
  const { currentDeck } = useSelector(state => state.currentDeckReducer);
  const repeatingInput = document.getElementById('cardsHolderRepeatingNumInput');

  useEffect(()=> {
    let aaa;
    aaa = vocabulary.filter(item => item.ruTopic.includes('Числа'));
    setWords(aaa);
  }, [])

  useEffect(()=> {
    let num1 = Math.floor(Math.random() * 100) + Math.floor(Math.random() * 10);
    console.log(num1);
    let meaning1;
    let transcription1;
    if (num1 < 21 && words !== null) {
      meaning1 = words.filter(item => item.num === num1)[0].word;
      transcription1 = words.filter(item => item.num === num1)[0].transcription;
    };
    if (num1 > 20 && num1 < 101 && num1 % 10 === 0) {
      meaning1 = words.filter(item => item.num === num1)[0].word;
      transcription1 = words.filter(item => item.num === num1)[0].transcription;
    };
    if (num1 > 20 && num1 < 100) {
      let tens1 = String(num1)[0];
      let tens2 = Number(tens1) * 10;
      let tens;
      tens = words.filter(item => item.num === tens2)[0].word;
      tensTranscription = words.filter(item => item.num === tens2)[0].transcription;
      let lastNum1 = num1 % 10;
      let lastNum;
    if (lastNum1 = 1) {
      lastNum = 'ואחת';
      transcription1 = `${tensTranscription} вэ ахАт`;
    };
    if (lastNum1 = 2) {
      lastNum = 'ושתיים';
      transcription1 = `${tensTranscription} у штАйм`;
    };
    if (lastNum1 = 3) {
      lastNum = 'ןשלוש';
      transcription1 = `${tensTranscription} вэ шалОш`;
    };
    if (lastNum1 = 4) {
      lastNum = 'וארבע';
      transcription1 = `${tensTranscription} вэ арбА`;
    };
    if (lastNum1 = 5) {
      lastNum = 'וחמש';
      transcription1 = `${tensTranscription} вэ хамЭш`;
    };
    if (lastNum1 = 6) {
      lastNum = 'ושש';
      transcription1 = `${tensTranscription} ва шеш`;
    };
    if (lastNum1 = 7) {
      lastNum = 'ושבע';
      transcription1 = `${tensTranscription} ва шЭва`;
    };
    if (lastNum1 = 8) {
      lastNum = 'ושמונה';
      transcription1 = `${tensTranscription} у шмОнэ`;
    };
    if (lastNum1 = 9) {
      lastNum = 'ותשע';
      transcription1 = `${tensTranscription} ва тЭша`;
    };  
      meaning1 = ''; `${tens}${lastNum}`
    }


    else {
      if (num1 > 21 && num1 < 101) {
        meaning1 = `${words.filter(item => item.num === num1[0]).word}`;
      } if (num1 > 21 && num1 < 101) {
      meaning1 = `${words.filter(item => item.num === num1[0]).word}`;
      }
    };
    console.log(meaning1);
    setCurrentNum({num: num1, meaning: meaning1});
  }, [])

  function makeUnswer() {
    let num1 = Math.floor(Math.random() * 100) + Math.floor(Math.random() * 10);
    let meaning1;
    if (num1 < 21 && words !== null) {
      meaning1 = words.filter(item => item.num === num1)[0].word;
    } else {
      if (num1 > 21 && num1 < 101) {
        meaning1 = `${words.filter(item => item.num[0] === num1)[0].word}`;
      } if (num1 > 21 && num1 < 101) {
      meaning1 = `${words.filter(item => item.num[0] === num1)[0].word}`;
      }
    };
    setCurrentNum({num: num1, meaning: meaning1});

  //Создать рандомное двузначное число;
  //Записать его в переменную;
  //Записать в переменную 
  }

  useEffect(()=> {
    makeUnswer()
  }, [])

  function word() {
    if (currentNum && currentNum !== null) {
    return (
    <>
      <div className="cardsHolder__title-raw">
        <p className="cardsHolder__title">
          {currentNum.num}
        </p>
      </div>
      {showNum && 
      <>
        <p className="cardsHolder__title">
          {currentNum.meaning}
        </p>
        {currentNum && currentNum !== null &&
          <p>
            Рекомендуется напечатать правильный вариант все равно: так он лучше запомнится
          </p>
        }
      </>}
    </>)}
  }

  function showTranslation() {
    setShowNum(true);
  };

  const onRepeatChange = e => {
    setMeaning({ ...repeatingForm, [e.target.name]: e.target.value });
  };

  useEffect(()=> {
    console.log(currentNum)
  }, [currentNum])

  useEffect(()=> {
    if (repeatingInput && (currentNum.meaning === repeatingForm.word)) 
    {setIsCorrect(true);
    repeatingInput.classList.add('cardsHolder__input_active');};
    if (repeatingInput && (currentNum.meaning !== repeatingForm.word) && isCorrect) {
      setIsCorrect(false);
      repeatingInput.classList.remove('cardsHolder__input_active');};
  }, [repeatingForm])

  function handleClick() {
    dispatch(removeCurrentDeck());
    navigate(`/`);
  }

  function nextWord1() {
    setShowNum(false);
    setIsCorrect(false);
    setMeaning({ ...repeatingForm, word: '' });
    repeatingInput.classList.remove('cardsHolder__input_active');
    makeUnswer();
  }

  function handleCloseModesClick() {
    setShowNum(false);
    setIsCorrect(false);
    setMeaning({ ...repeatingForm, word: '' });
    repeatingInput.classList.remove('cardsHolder__input_active');
    navigate('/topics/Numbers');
  }

  return (
    <>
      <Header />
      <main className="main__content1">
        <section className="deckItem__content">
          <div className="deckItem__title-container">
            <h1 className="deckItem__title">
              Запоминаем числа
            </h1>
            <div className="deckItem__title-container1" onClick={handleClick}>
              <img 
                src={arrow} 
                alt="Стрелка назад" 
                className='deckItem__arrow'
              />
              <p className="deckItem__text">На главную</p>
            </div>
          </div>
          <div className='cardsHolder__form'>
            <div>
              {word()}
            </div>
            <input 
              placeholder="Место для слова" 
              value={repeatingForm.word} 
              id='cardsHolderRepeatingNumInput'
              type="text"
              name="word" 
              onChange={onRepeatChange}
              required
              className='cardsHolder__input'
              autoComplete="off"
              autoCorrect="off" 
              autoCapitalize="off" 
              spellCheck="false" />
            <div className='cardsHolder__button-block'>
              <button 
                className='cardsHolder__button'
                onClick={showTranslation}>
                  Показать ответ
              </button>
              <button 
                className='cardsHolder__button'
                onClick={nextWord1}>
                  Следующее число
              </button>
            </div>
          </div>
          <div className="deckItem__arrow-container" onClick={handleCloseModesClick}>
            <img 
              src={arrow} 
              alt="Стрелка назад" 
              className='deckItem__arrow'
            />
            <p className="deckItem__text">Назад к теме</p>
          </div>
        </section>
      </main>
      <Footer />
    </>
  );
}  

export default NumbersTrainPage;
