import React, { useState } from "react";
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import around from '../../../images/around.png';
import './WordCard.css';
import { current } from "@reduxjs/toolkit";
import { setCurrentWord} from '../../../services/actions/currentDeck.js';

function WordCard(props) {

  const {item, cardsAreVisible } = props;
  const { currentDeck } = useSelector(state => state.currentDeckReducer);
  const navigate = useNavigate();

  const [hintIsVisible, setHintIsVisible] = useState(false);
  const [backIsVisible, setBackIsVisible] = useState(false);

  function handleClick() {
    setHintIsVisible(!hintIsVisible);
  }

  function handleIconClick() {
    setBackIsVisible(!backIsVisible);
  }

  function setText() {
    if (hintIsVisible) return `${item.transcription}`; else return 'Подсказка'
  }

  function setVerb() {
    if (currentDeck && currentDeck.enTopic === 'Verbs') {
      setCurrentWord(item);
      navigate(`/topics/Verbs/${item.translation}`);
    }
  }

  return (
    <div className="wordCard">
      {!backIsVisible && 
      <>
        {cardsAreVisible &&         
        <p className="word">
          {item.translation}
        </p>}
        {!cardsAreVisible && 
        <>
          <p className="word">
            {item.vocalization}
          </p>
          <p className="translation">
            {item.translation}
          </p>
          <p className="translation">
            {item.transcription}
          </p>
        </>
        }
        {cardsAreVisible && 
        <>
          <p className="translation1" onClick={handleClick}>
            {setText()}
          </p>
          <img 
            src={around} 
            alt="Стрелка переворота" 
            className='wordCard__img'
            onClick={handleIconClick}
          />
        </>
        }
      </>
      }
      {backIsVisible && 
      <>
        <p className="word">
          {item.vocalization}
        </p>
        <p className="translation">
          {item.transcription}
        </p>
        <img 
          src={around} 
          alt="Стрелка переворота" 
          className='wordCard__img'
          onClick={handleIconClick}/>
        </>
      }
      {currentDeck && currentDeck.enTopic === 'Verbs' &&         
        <p className="text" onClick={setVerb}>
          Смотреть формы
        </p>}
    </div>
  );
}  

export default WordCard;
