import React, { useEffect, useState } from "react";
import { Routes, Route } from 'react-router-dom';
import Main from '../Main/Main.jsx';
import PageNotFound from '../PageNotFound/PageNotFound.jsx';
import DeckItemPage from '../DeckItemPage/DeckItemPage.jsx';
import WordItemPage from '../WordItemPage/WordItemPage.jsx';
import NumbersTrainPage from '../NumbersTrainPage/NumbersTrainPage.jsx';
import './App.css';
import { useSelector, useDispatch } from 'react-redux';
import { vocabulary, topics, verbs } from "../../utils/constants.js";
import { removeCurrentDeck, setCurrentDeck, setCurrentWord  } from '../../services/actions/currentDeck.js';

function App() {

  const { currentDeck } = useSelector(state => state.currentDeckReducer);
  const { currentWord } = useSelector(state => state.currentDeckReducer);
  const dispatch = useDispatch();

  useEffect(()=> {
    let aa;
    let aaa;
    console.log(window.location.pathname.slice(8));
    console.log(window.location.pathname.slice(14));
    const adress = window.location.pathname.slice(8);
    const firstPos = adress.indexOf('/');
    console.log(firstPos);
    console.log(window.location.pathname.slice(9 + firstPos));
    console.log(window.location.pathname.substr(8, firstPos));
    if (firstPos !== -1) {
      aa = topics.filter(item => item.enTopic === window.location.pathname.substr(8, firstPos));
      aaa = verbs.filter(item => item.enTranslation === window.location.pathname.slice(9 + firstPos));
      console.log(aaa);
    }
    else {
      aa = topics.filter(item => item.enTopic === window.location.pathname.slice(8));
    };
    console.log(aa);
    if (aa !== undefined && aa) {
      dispatch(setCurrentDeck(aa[0]));
    };
    if (aaa !== undefined && aaa) {
      console.log(aaa[0]);
      dispatch(setCurrentWord(aaa[0]));
    }
/*
    if (-1 < firstPos) {
      aaa = verbs.filter(item => item.enTranslation === window.location.pathname.slice(9 + firstPos));
      console.log(aaa);

    }

    if (aa !== undefined && aa) {
      dispatch(setCurrentDeck(aa[0]));
    };
    console.log(aa);
    console.log(aa && aa.length === 0);
    console.log(window.location.pathname.slice(8).startsWith('Verbs/'));
    if (aa && aa.length === 0 && window.location.pathname.slice(8).startsWith('Verbs/')) {
      let aaa = verbs.filter(item => item.enTranslation === window.location.pathname.slice(14));
      console.log(aaa);
      dispatch(setCurrentDeck({
        ruTopic: 'Глаголы',
        enTopic: 'Verbs',
      }));
      dispatch(setCurrentWord(aaa[0]));
    }*/
  }, [])

  return (
    <>
      <div className="page">
        <Routes>
          <Route 
            exact path="/" 
            element={
              <Main />
            }>  
          </Route>
          <Route 
            path="*" 
            element={
              <PageNotFound/>
            }>
          </Route>
          <Route 
            path="/numbers" 
            element={
              <NumbersTrainPage/>
            }>
          </Route>
          {currentDeck !==null && 
          <Route 
            path="/topics/:deckItem" 
            element={
              <DeckItemPage />
            }
          >
          </Route>}
          {currentWord !==null && 
          <Route 
            path="/topics/Verbs/:wordItem" 
            element={
              <WordItemPage />
            }
          >
          </Route>}
          {currentWord !==null && 
          <Route 
            path="/topics/All/:wordItem" 
            element={
              <WordItemPage />
            }
          >
          </Route>}
          {currentWord !==null && 
          <Route 
            path="/topics/:deckItem/:wordItem" 
            element={
              <WordItemPage />
            }
          >
          </Route>}
        </Routes>
      </div>
    </>
  );
}

export default App;
